-- Create RPC function for fast server-side category aggregation
CREATE OR REPLACE FUNCTION get_hsk_categories(level int)
RETURNS TABLE (category varchar, count bigint) AS $$
BEGIN
  RETURN QUERY
  SELECT hv.category, COUNT(*)::bigint
  FROM hsk_vocabulary hv
  WHERE hv.hsk_level = level
  GROUP BY hv.category
  ORDER BY hv.category;
END;
$$ LANGUAGE plpgsql;

